
public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// The problem in while loop, we had to take a number which act as a counter
		int n = 10, sum = 0;
		
		for (int i = 0 ; i<=n ; i++) {
			sum = sum+i;
		}
		
		System.out.println("Sum is = "+sum);
	}

}
