
public class StringFunctionsMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = "Aditya";
		
		System.out.println("The Length of String is :"+name.length());

		System.out.println("The Uppercase of String is :"+name.toUpperCase());

		System.out.println("The Lowercase of String is :"+name.toLowerCase());

		System.out.println("The charAt of String is :"+name.charAt(2));
		
		System.out.println("The substring of String is :"+name.substring(2));
		
		System.out.println("The substring of String is :"+name.substring(1, 3));



		// String is a Pallindrome
		

	}

}
